mapper.insert:
INSERT INTO EVAL_BDD_CMP(
						BDD_CMP_ID, 
						SHT_INFO_ID, 
						BDD_CMP_NM, 
						BDD_CMP_NBR
						)VALUES(
						#{bddCmpId}, 
						#{shtInfoId}, 
						#{bddCmpNm}, 
						#{bddCmpNbr}
						);

mapper.update:
UPDATE 
		EVAL_BDD_CMP
SET 	 
		BDD_CMP_NM=#{bddCmpNm}, 
		BDD_CMP_NBR=#{bddCmpNbr}
WHERE 	BDD_CMP_ID=#{bddCmpId}

mapper.select:
SELECT BDD_CMP_ID
FROM EVAL_BDD_CMP
WHERE SHT_INFO_ID = #{shtInfoId}
